    validation = [];
    disabled = false;
    maxFileCount;
    fileInput;
    clearFileInput() {
        this.fileInput.nativeElement.value = '';
    }
    checkedUploadCloud;
    checkedGenAttachmentIcon;
    checkedPptIcon;
    checkedXlsIcon;
    _toast = inject(ToastService);
    blobUrlCache = new Map();
