        this.checkedGenAttachmentIcon = this.sanitizer.bypassSecurityTrustHtml(IconAttachmentSVGIcon);
        this.checkedPptIcon = this.sanitizer.bypassSecurityTrustHtml(PPT_SVGIcon);
