    _isCollapsed = signal(false);
    constructor() {
        // Initialize from sessionStorage on service creation
        const storedState = sessionStorage.getItem('isCollapsed');
        if (storedState === 'true') {
            this._isCollapsed.set(true);
        }
        this.listenToWindowResize();
    }
